 Function Set-DetectionMethodNOGUI{
                    Param(
                    [String] $productCode,
                    [String] $RegistryKey
                    
                    )

 $Global:ProductCode=$productCode;
 $Global:RegistryKey=$RegistryKey;

 
 if([String]::IsNullOrEmpty($Global:ProductCode) -and  ![String]::IsNullOrEmpty($Global:RegistryKey)){
  $DetectionScriptBlock="
 if([String]::IsNullOrEmpty((Get-Item Registry::$Global:RegistryKey -ErrorAction SilentlyContinue))){
                                                                                                                            
 Write-Host 'Not installed'

 }else{Write-Host 'Installed'}"
 $DetectionScriptBlock.ToString() | Out-File -FilePath $env:TEMP\Detection.txt
 [bool]$Global:TextDetection=$true
}elseif(![String]::IsNullOrEmpty($Global:ProductCode) -and  ![String]::IsNullOrEmpty($Global:RegistryKey)){
                                                                                                        
