   Write-Host 'Installed'

  }else{Write-Host 'Not installed'}"
$DetectionScriptBlock.ToString() | Out-File -FilePath $env:TEMP\Detection.txt
 [bool]$Global:TextDetection=$true

 }else{[bool]$Global:TextDetection=$false;}

 }



