Function IsConsoleInstalled{
if([String]::IsNullOrEmpty($env:SMS_ADMIN_UI_PATH)){Return $false;} Else{Return $true}
     
}

Function VerifyApplicationName{
                                Param(
                                    [String]$ApplicationName,
                                    [String]$SiteCode,
                                    [String]$SiteServer,
                                    [String]$MethodofCreation

                                )
                                if($MethodofCreation -eq 'Application'){
                                $Application_="'"+$ApplicationName+"'"
                                $GetApplicationName=Get-WmiObject -Namespace Root\SMS\Site_$SiteCode -ComputerName $SiteServer -Query "Select * From SMS_Application Where LocalizedDisplayName=$Application_" -ErrorAction SilentlyContinue
                                if([String]::IsNullOrEmpty($GetApplicationName)){Return $true} Else{Return $false}
                                }
                                if($MethodofCreation -eq 'Package'){Return $true}
                                
}

Function CheckParameterSelected{
							Param([String]$SiteCode,
									[String]$Applicationname,
									[String]$ContentPath,
									[String]$SiteServer,
                                    [String]$InstallationProgram,
                                    [String]$Method,
                                    [String]$CollectionType,
                                    [String]$AppType
							)
if([String]::IsNullOrEmpty($SiteCode)){Return 'SiteCode is not selected'} elseif([String]::IsNullOrEmpty($CollectionType)){Return 'Select Collection type'}elseif([String]::IsNullOrEmpty($Applicationname)){Return 'Application name is not Specified'}elseif([String]::IsNullOrEmpty($ContentPath)){Return 'Select source UNC path'}elseif([String]::IsNullOrEmpty($SiteServer)){Return 'Site Server Name is not specified'}elseif($AppType -ne 'MSI'){if([String]::IsNullOrEmpty($InstallationProgram)){Return 'Installation Command is not specified'}}
elseif($(VerifyApplicationName -SiteCode $SiteCode -ApplicationName $Applicationname -SiteServer $SiteServer -MethodofCreation $Method) -eq $true){Return $true}Else{Return 'Application Name must be unique'}
     
}

