Function BuildCollectionQuery{

        param(
        [string] $AdGroup,
        [String] $Type,
        [String] $Domain
        )


$TempADGroup='"'+'INT'+'\\'+$AdGroup+'"'
if($Type -eq 'Device'){


$Query="select *  from  SMS_R_System where SMS_R_System.SecurityGroupName = $TempADGroup"

Return $Query;

}
elseif($Type -eq 'User'){

$Query="select *  from  SMS_R_User where SMS_R_User.SecurityGroupName = $TempADGroup"

Return $Query;

}

}

function CreateNew-ADGroup{
                    param(
                    [String]$DC,
                    [String]$NewGroupName,
                    #[String]$OU,
                    [String] $Phase
                )

            #OU=Pilot,OU=Application Distribution Group,DC=MyLab,DC=net

        #Verify Existing group
        $Searcher = New-Object System.DirectoryServices.DirectorySearcher
        $Searcher.Filter = "(&(objectCategory=group)(sAMAccountName=$NewGroupName))"

        $Searcher.SearchRoot = 'LDAP://DC=int,DC=Labtest,DC=com'

        $Getname=$Searcher.FindAll()

         if([String]::IsNullOrEmpty($Getname)){

                        $Global:ADGroupExist=$False


                        #Create AD group


            Switch($Phase){
                'Pilot'{

                        $NewGroup=[adsi]"LDAP://$DC/OU=Pilot,OU=Application Distribution Group,OU=GROUPS,OU=ENTERPRISE,DC=int,DC=Labtest,DC=com"

                        $GroupName=$NewGroup.create('Group',"CN=$NewGroupName")
                        $GroupName.put("sAMAccountName", "$NewGroupName")
                        $GroupName.setinfo()
                        break;
                    }

                'Prod'{

                        $NewGroup=[adsi]"LDAP://$DC/OU=Production,OU=Application Distribution Group,OU=GROUPS,OU=ENTERPRISE,DC=int,DC=Labtest,DC=com"

                        $GroupName=$NewGroup.create('Group',"CN=$NewGroupName")
                        $GroupName.put("sAMAccountName", "$NewGroupName")
                        $GroupName.setinfo()
                        break;
                }

                'UAT'{

                    $NewGroup=[adsi]"LDAP://$DC/OU=UAT,OU=Application Distribution Group,OU=GROUPS,OU=ENTERPRISE,DC=int,DC=Labtest,DC=com"

                    $GroupName=$NewGroup.create('Group',"CN=$NewGroupName")
                    $GroupName.put("sAMAccountName", "$NewGroupName")
                    $GroupName.setinfo()
                    break;
                }

        }
    }else{$Global:ADGroupExist=$true;ShowMessage -message "$NewGroupName is already exist" -gui $false }

}

