function ZeroTouchApplicationCreation{

#ShowMessage -message "Getting current directory" -Gui $false


if(![String]::IsNullOrEmpty($Global:CurrentPath)){ShowMessage -message "Running from directory $CurrentPath" -Gui $false}



ShowMessage -message 'Verifying Application Template csv file' -Gui $false

$ApplicationTemplatefile="$Global:CurrentPath\ApplicationTemplate.csv"

$GetApplicationInfo=Import-Csv -Path $ApplicationTemplatefile           #C:\ApplicationSources\ZTD\ZTD\ApplicationTemplate.csv

if([String]::IsNullOrEmpty($GetApplicationInfo)){$global:AutoCreate=$false;ShowMessage -message 'Fail to read Application Template csv file. Please verify and place the file in desire location before trigger auto create method.' -Gui $false}
else{$global:AutoCreate=$true;ShowMessage -message 'Application template loaded successfully' -Gui $false}

#Write-Host "Total Application or Package to be created $($GetApplicationInfo.count)" -ForegroundColor Yellow
ShowMessage -message "Total Application or Package to be created $($GetApplicationInfo.count)" -gui $false
$GetApplicationInfo | % {
        

        $ApplicationName=$($_.PackageName)
        $PackageType=$_.PackageType
        $ApplicationPublisher=$_.Publisher
        $ApplicationVersion=$_.Version
        $ApplicationType=$_.ApplicationType
        $DeploymentCollectionType=$_.CollectionType
        $ApplicationSource=$_.PackageSource
        $InstallationProgram=$_.InstallationProgram
        $UninstallationProgram=$_.UninstallationProgram
        $OSRequirement=$_.OSRequirement
        $Global:ProductCode=$_.'DetectionMethod(ProductCode)'
        $Global:RegKey=$_.'DetectionMethod(Reg Key)'
        $DeploymentPurpose=$_.DeploymentPurpose

        #Build AD group Name

        #Write-Host "Printing Application name SL. $i" -ForegroundColor Yellow
        ShowMessage -message "Creating Application $ApplicationName" -gui $false
        #Write-Host $ApplicationName $PackageType $ApplicationPublisher $ApplicationVersion $ApplicationType $DeploymentCollectionType $ApplicationSource $InstallationProgram -ForegroundColor Green

        #Limiting Collection Name

        if($DeploymentCollectionType -eq 'User'){$LimittingCollection='All Users'}Else{$LimittingCollection='All Systems'}




#Write-Host "Printing Application name SL. 1" -ForegroundColor Yellow

#Write-Host $ApplicationName $PackageType $ApplicationPublisher $ApplicationVersion $ApplicationType $DeploymentCollectionType $ApplicationSource $InstallationProgram -ForegroundColor Green
        if($PackageType -eq 'Application'){
                    $ApplicationName_="'"+$ApplicationName+"'"
                    $GetApplicationName=Get-WmiObject -Namespace Root\SMS\Site_$SiteCode -ComputerName $SiteServer -Query "Select * From SMS_Application Where LocalizedDisplayName=$ApplicationName_" -ErrorAction SilentlyContinue
        if([String]::IsNullOrEmpty($GetApplicationName)){
                    Write-Host "Creating Application for $ApplicationName" -ForegroundColor Green
                    Set-DetectionMethodNOGUI -productCode $Global:ProductCode -RegistryKey $Global:RegKey
                    CreateApplication -AppType $ApplicationType -SiteCode $SiteCode -SiteServer $SiteServer -Publisher $ApplicationPublisher -Version $ApplicationVersion -ApplicationName $ApplicationName -ApplicationContent $ApplicationSource -InstallProgram $InstallationProgram -UninstallProgram $UninstallationProgram -Requirements $(SetRequirements -Sitecode $SiteCode -SiteProvider $SiteCode -Requirements $OSRequirement -Method 'Application') -ISTextDetection $global:TextDetection
        }Else{ Write-Host "$ApplicationName is already exit" -ForegroundColor Green}
        Create-Collection -CollectionName $("$ApplicationName"+ "$ApplicationVersion") -Publisher $ApplicationPublisher -CollectionType $DeploymentCollectionType -LimitingCollection $LimittingCollection 
        #Create-Collection -CollectionName $("$ApplicationName"+ "-UAT") -Query $(BuildCollectionQuery -AdGroup $UATADGroupName -Type $DeploymentCollectionType -Domain $global:DC) -CollectionType $DeploymentCollectionType -LimitingCollection $LimittingCollection -Phase 'UAT'
        #Create-Collection -CollectionName $("$ApplicationName"+ "-Prod") -Query $(BuildCollectionQuery -AdGroup $ProdADGroupName -Type $DeploymentCollectionType -Domain $global:DC) -CollectionType $DeploymentCollectionType -LimitingCollection $LimittingCollection -Phase 'Prod'


        ShowMessage -message "$ApplicationName has been created" -gui $false
        ShowMessage -message "Create Deployment for $ApplicationName" -gui $false
        CreateDeployment -ApplicationName $ApplicationName -PackageName $ApplicationName -Type Application -DPGroups $Global:DPGroup -Collection $("$ApplicationName"+ "$ApplicationVersion") -AvailableMethod $DeploymentPurpose -SiteServer $SiteServer -SMSSiteCode $SiteCode -AvailableTime $(Get-Date) -deadlineTime $(Get-Date) -Distribute $true

        }else{

            Write-Host "Creating Package for $ApplicationName" -ForegroundColor Green
            CreatePackage -Packagename $ApplicationName -Path $ApplicationSource -Publisher $ApplicationPublisher -Version $ApplicationVersion -InstallCommand $InstallationProgram -SiteCode $SiteCode -SiteServer $SiteServer
            SetRequirements -Sitecode $SiteCode -SiteProvider $SiteServer -Requirements $OSRequirement -Method 'Package' -PackageName $ApplicationName
            Create-Collection -CollectionName $("$ApplicationName"+ "$ApplicationVersion") -Publisher $ApplicationPublisher  -CollectionType $DeploymentCollectionType -LimitingCollection $LimittingCollection
            #Create-Collection -CollectionName $("$ApplicationName"+ "-UAT") -Query $(BuildCollectionQuery -AdGroup $UATADGroupName -Type $DeploymentCollectionType -Domain $global:DC) -CollectionType $DeploymentCollectionType -LimitingCollection $LimittingCollection -Phase 'UAT'
            #Create-Collection -CollectionName $("$ApplicationName"+ "-Prod") -Query $(BuildCollectionQuery -AdGroup $ProdADGroupName -Type $DeploymentCollectionType -Domain $global:DC) -CollectionType $DeploymentCollectionType -LimitingCollection $LimittingCollection -Phase 'Prod'

            ShowMessage -message "$ApplicationName has been created" -gui $false
            ShowMessage -message "Create Deployment for $ApplicationName" -gui $false
            CreateDeployment -ApplicationName $ApplicationName -PackageName $ApplicationName -Type Package -DPGroups $Global:DPGroup -Collection $("$ApplicationName"+ "$ApplicationVersion") -AvailableMethod $DeploymentPurpose -SiteServer $SiteServer -SMSSiteCode $SiteCode -AvailableTime $(Get-Date) -deadlineTime $(Get-Date) -Distribute $true

        }
}


